from PySide6.QtGui import QIcon, QPixmap, QPainter
from PySide6.QtSvg import QSvgRenderer
from PySide6.QtCore import QByteArray, Qt, QSize
from typing import Optional, Dict, Tuple
import re

class IconProvider:
    def __init__(self):
        
        """
        Initialize the IconProvider instance.

        This constructor sets up the caches for storing icons and SVG renderers
        to optimize performance and reduce redundant processing.

        Attributes:
            _icon_cache (Dict[Tuple[str, Optional[str], int], QIcon]): Cache for
                storing QIcon objects keyed by a tuple of icon name, optional color,
                and size.
            _svg_renderer_cache (Dict[str, QSvgRenderer]): Cache for storing
                QSvgRenderer objects keyed by the SVG data string.
        """

        self._icon_cache: Dict[Tuple[str, Optional[str], int], QIcon] = {}
        self._svg_renderer_cache: Dict[str, QSvgRenderer] = {}

    def get_icon(
        self,
        name: str,
        color: str = None,
        size: int = 24,
        fallback: str = "QuestionMark"
    ) -> QIcon:
        """Get an icon with optional color and size customization."""
        cache_key = (name, color, size)
        
        # Return cached icon if available
        if cache_key in self._icon_cache:
            return self._icon_cache[cache_key]
        
        # Get SVG data (fallback if not found)
        svg_data = ICONS.get(name, ICONS.get(fallback, ""))
        if not svg_data:
            return QIcon()
        
        # Apply color if specified
        if color:
            svg_data = self._apply_svg_color(svg_data, color)
        
        # Scale SVG if needed
        if size != 24:
            svg_data = self._scale_svg(svg_data, size)
        
        # Create icon
        icon = self._svg_to_icon(svg_data)
        
        # Cache the icon
        self._icon_cache[cache_key] = icon
        return icon

    def _apply_svg_color(self, svg_data: str, color: str) -> str:
        """Replace 'currentColor' with the specified color."""
        return re.sub(
            r'stroke="currentColor"',
            f'stroke="{color}"',
            svg_data
        )

    def _scale_svg(self, svg_data: str, size: int) -> str:
        """Scale SVG to the specified size."""
        return re.sub(
            r'(width|height)="24"',
            f'\\1="{size}"',
            svg_data
        )

    def _svg_to_icon(self, svg_data: str) -> QIcon:
        """Convert SVG string to QIcon."""
        renderer = self._svg_renderer_cache.get(svg_data)
        if not renderer:
            renderer = QSvgRenderer(QByteArray(svg_data.encode("utf-8")))
            self._svg_renderer_cache[svg_data] = renderer
        
        size = renderer.defaultSize()
        pixmap = QPixmap(size)
        pixmap.fill(Qt.transparent)
        
        painter = QPainter(pixmap)
        renderer.render(painter)
        painter.end()
        
        return QIcon(pixmap)